package classwork;

public class nested 
{

	public static void main(String[] args) 
	{
	
	char n='A';
		// TODO Auto-generated method stub
		
		for(int i=1;i<=5;i++)
		{
		
			n='A'; 
			for ( int j=5;j>=i;j--)
			{
				System.out.print("n");
				n++;
			
				
			System.out.println();
			}
		}
	}
}
	
